mapper.select:
SELECT LEVEL FROM DUAL

	WHERE LEVEL >= #{gwanSfl}
	CONNECT BY LEVEL <= #{gwanEfl}

mapper.update:
UPDATE COLLEGE
    SET
        COL_GWAN = #{colGwan,jdbcType=VARCHAR}
WHERE
    COL_ID = #{colId}

UPDATE DEPARTMENT
	SET
		DEPT_FL = #{deptFl,jdbcType=NUMERIC}
WHERE
	DEPT_ID = #{deptId}
