mapper.select:
SELECT A.*
FROM USERS A INNER JOIN CLS B ON(A.USER_NO = B.USER_NO)
WHERE LEC_SEMS=#{lecSems} AND LEC_ID=#{lecId}

SELECT COUNT(*)
FROM LECBO C
WHERE LEC_SEMS=#{detailCondition.lecSems} AND LEC_ID=#{detailCondition.lecId}

SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM( select C.lecbo_no ,C.lecbo_info ,C.lecbo_title ,C.lecbo_content ,C.lecbo_hit ,TO_CHAR(C.lecbo_date, 'YYYY-MM-DD HH:MI:ss') as lecbo_date ,C.lec_sems ,C.lec_id ,C.user_no ,C.lec_div ,D.USER_NAME FROM LECBO C INNER JOIN USERS D ON (C.USER_NO = D.USER_NO) where C.LEC_SEMS=#{detailCondition.lecSems} AND C.LEC_ID=#{detailCondition.lecId}
ORDER BY lecbo_no DESC )A )B
WHERE RNUM >=#{startRow} AND RNUM <=#{endRow}

mapper.update:
UPDATE LECBO
SET LECBO_HIT = LECBO_HIT + 1
WHERE LECBO_NO = #{lecboNo}

mapper.insert:
SELECT LECBO_SEQ.NEXTVAL FROM DUAL
INSERT INTO LECBO ( LECBO_NO ,LECBO_TITLE ,LECBO_CONTENT ,LECBO_HIT ,LECBO_DATE ,LEC_SEMS ,LEC_ID ,USER_NO
) VALUES ( #{lecboNo} ,#{lecboTitle} ,#{lecboContent} ,0 ,SYSDATE ,(SELECT SEMS_NO FROM SEMSDATA
WHERE SYSDATE >= SEMS_SDATE AND SYSDATE <= SEMS_EDATE)
,#{lecId} ,#{userNo}
)

mapper.update:
UPDATE LECBO
SET LECBO_TITLE = #{lecboTitle}
	,LECBO_CONTENT = #{lecboContent}
WHERE LECBO_NO = #{lecboNo}
