mapper.update:
update LECQNA 
SET LECBO_HIT = LECBO_HIT +1
WHERE LECBO_NO = #{lecboNo}

mapper.insert:

	 		SELECT NVL(MAX(LECBO_NO),0)+1
	 		FROM LECQNA


	 INSERT INTO LECQNA
	 (
	 	    LECBO_NO
		   ,LEC_SEMS
		   ,LEC_ID

		   ,PRO_NO		   

		   ,LECBO_TITLE
		   ,LECBO_CONTENT
		   ,LECBO_DATE
		   ,LECBO_PASS

		   ,STU_NO


	 )
	 VALUES
	 (
	 	#{lecboNo}
	   ,(SELECT SEMS_NO
	        FROM SEMSDATA

		        WHERE SYSDATE >= SEMS_SDATE 
		        AND SYSDATE <= SEMS_EDATE) 
	 	,#{lecId}

	   ,#{proNo}		   

		,#{lecboTitle}
	   ,#{lecboContent}
	   ,SYSDATE
	   ,#{lecboPass}	 

  	   ,#{stuNo}


	 )

mapper.update:
UPDATE LECQNA
SET 
		LEC_ID = #{lecId,jdbcType=VARCHAR}
		,LECBO_TITLE = #{lecboTitle,jdbcType=VARCHAR}
		,LECBO_CONTENT = #{lecboContent,jdbcType=CLOB}		
WHERE LECBO_NO=#{lecboNo}

mapper.select:
SELECT A.*, DECODE(B.PRO_NO,NULL,'미등록','등록') AS CODE
FROM LECQNA A LEFT OUTER JOIN LECREPLY B ON (A.LECBO_NO = B.LECBO_NO)AND B.STU_NO IS NULL
WHERE A.STU_NO = #{stuNo}
